import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import {
	StyleSheet,
	Text,
	View,
	TextInput,
	TouchableOpacity,
    FlatList,
    KeyboardAvoidingView,
    Platform,
    Keyboard,
    Alert

} from 'react-native';

import { Ionicons, MaterialIcons } from '@expo/vector-icons';

export default function App() {

	const [task, setTask] = useState(['Vitor','Renan','Macedo']);
    const [newTask, setNewTask] = useState('');

    async function addTask() {

        if (newTask === ''){
            return;
        }

        const search = task.filter(task => task === newTask);

        if(search.length !== 0){
            Alert.alert("Atenção!", "Nome da tarefa repetido");
            return;
        }

        setTask([... task, newTask]);
        setNewTask('');
        Keyboard.dismiss();
    }

    // useEffect(() => {
    //     console.log(newTask);
    // }, [newTask])

  return (
    <>
    <KeyboardAvoidingView
        keyboardVerticalOffset={0}
        behavior="padding"
        style={{ flex: 1 }}
        enabled={ Platform.OS === 'ios'}
    >
    <View style={styles.container}>
			<View style={styles.Body}>
      	        <FlatList
					style={styles.FlatList}
					data={task}
					keyExtractor={ item => item.toString()}
					showsVerticalScrollIndicator={false}
					renderItem={({ item }) => (
                        <View style={styles.ContainerView}>
                            <Text style={styles.Texto}>{ item }</Text>
                            <TouchableOpacity>
                                <MaterialIcons name="delete-forever" size={25} color="#f64c75" />
                            </TouchableOpacity>
                        </View>
                    )}
				/>
			</View>
			<View style={styles.Form}>
				<TextInput 
					style={styles.Input}
					placeholderTextColor="#999"
					placeholder="Adicione uma tarefa"
					autoCorrect={true}
                    maxLength={25}
                    onChangeText={text => setNewTask(text)}
                    value={newTask}
				/>
				<TouchableOpacity style={styles.Button} onPress={() => addTask()}>
					<Ionicons name="ios-add" size={25} color="#fff" />
				</TouchableOpacity>
			</View>
    </View>
    </KeyboardAvoidingView>
    </>
  );
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
		backgroundColor: '#fff',
		paddingHorizontal: 20,
		paddingVertical: 20,
		marginTop: 20
	},
	Body: {
		flex: 1
	},
	Form: {
		padding: 0,
		height: 60,
		justifyContent: 'center',
		alignSelf: "stretch",
		flexDirection: "row",
		paddingTop: 13,
		borderTopWidth: 1,
		borderColor: '#eee'
	},
	Input: {
		flex: 1,
		height: 40,
		backgroundColor: '#eee',
		paddingLeft: 10,
		borderRadius: 4
	},
	Button: {
		height: 40,
		width: 40,
		justifyContent: 'center',
		alignItems: 'center',
		backgroundColor: '#1c6cce',
		borderRadius: 4,
		marginLeft: 10
	},
	FlatList: {
		flex: 1,
		marginTop: 5,

    },
    ContainerView: {
        marginBottom: 15,
        padding: 15,
        borderRadius: 4,
        alignItems: 'center',
        backgroundColor: "#eee",
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        borderWidth: 1,
        borderColor: "#eee"
    },
    Texto: {
        fontSize: 14,
        color: '#333',
        fontWeight: 'bold',
        textAlign: 'center'
    }
});
